import './style.scss';
import React, { useState,} from "react";

function App() {
   const [isActive, setIsActive] = useState(false)
   const [searchValue, setSearchValue] = useState('')
   const [weather, setWeather] = useState({})

   function toggleClass() {
        setIsActive(!isActive)      
    }

 async function renderData() {
  try {
    fetch(
      `http://api.openweathermap.org/data/2.5/weather?q=${searchValue}&appid=1d1d06b0328958cd6e4ba9dc187233d9`)
      .then(res => res.json())
      .then(result => {
        setWeather(result);
        setSearchValue('')
        console.log(result)    
      }
        );
    } catch(err) {
      alert("Data not found please try with a valid city name")

    }
    }

  async function renderDataKey(e) {
    if(e.key === "Enter") {
      try {
        fetch(
          `http://api.openweathermap.org/data/2.5/weather?q=${searchValue}&appid=1d1d06b0328958cd6e4ba9dc187233d9`)
          .then(res => {

            if (res.ok) {
              console.log(res.status);
              return res.json();
            } else {
              if (res.status === 404) {
                return alert("Oops, there seems to be an error!(wrong location)");
              }
              alert("Oops, there seems to be an error!");
              throw new Error("You have an error");
            }
          })
          .then(result => {
            setWeather(result);
            setSearchValue('')
            console.log(result)    
          }
            );
        } catch(err) {
          alert(err)
        }
     }
    }

  return (
    <div className="App">
      <div className="inputContainer">
        <label for="userInput">Search location</label>
        <input type="text" value={searchValue} onChange={e => setSearchValue(e.target.value)} onKeyPress={renderDataKey}/>
        <button type="submit" className="searchBtn"  onClick={renderData}>Search</button>

        <div className="containerToggle">
            <p>Farhenheit</p>
            <div className="container">
                <div className={isActive ? "toggled-btn" : "toggle-btn"} onClick={() => toggleClass()}>
                    <div className="inner-circle"></div>
                </div>
            </div>
            <p>Celsius</p>
           </div>
          </div>
          {(typeof weather.main != "undefined") ? (
            <div className="result-container">
              <div>{weather.name}</div>
              <div>{weather.main.temp}</div>
              <div>{weather.weather[0].description}</div>
              <div>{weather.feels_like}</div>
              <div>{weather.main.temp_min}</div>
              <div>{weather.main.temp_max}</div>
              <div>{weather.main.humidity}</div>
              <div>{weather.wind.speed}</div>
            </div>

          ) : ('')}
    </div>
  );

}

export default App;
