import './style.scss';
import React, { useState } from "react";

function App() {
   const [isActive, setIsActive] = useState(false)
   const [searchValue, setSearchValue] = useState()
   const [weather, setWeather] = useState()
   function toggleClass() {
        setIsActive(!isActive)      
    }

    async function processData() {
      const userInput = searchValue;
      console.log(userInput)
      const response = await fetch('https://api.openweathermap.org/data/2.5/weather?q='+userInput+'&appid=1d1d06b0328958cd6e4ba9dc187233d9', 
      {mode: 'cors'})
      const weatherData = await response.json();
      console.log(weatherData);
      setWeather(weatherData)
  }


  return (
    <div className="App">
        <form id="form" action="" onSubmit={() => processData()}>
            <label for="userInput">Search location</label>
        <input type="text" className="userInput" name="userInput" onChange={(e) => setSearchValue(e.target.value)}></input>
        <button type="submit" className="searchBtn">Search</button>
        </form>

        <div className="containerToggle">
            <p>Farhenheit</p>
            <div className="container">
                <div className={isActive ? "toggled-btn" : "toggle-btn"} onClick={() => toggleClass()}>
                    <div className="inner-circle"></div>
                </div>
            </div>
            <p>Celsius</p>
        </div>

        <img id="imgLoad" src="./loading-buffering.gif" alt="" srcset=""></img>


        <div className="displayerWeather"></div>
        <img className="weathImg" src="" alt=""></img>

    </div>
  );
}

export default App;
