import './style.scss';
import React, { useState } from "react";

function App() {
   const [isActive, setIsActive] = useState(false)

   function toggleClass() {
        setIsActive(!isActive)      
    }

  return (
    <div className="App">
        <form id="form" action="">
            <label for="userInput">Search location</label>
        <input type="text" className="userInput" name="userInput"></input>
        <button type="submit" className="searchBtn">Search</button>
        </form>

        <div class="containerToggle">
            <p>Farhenheit</p>
            <div class="container">
                <div className={isActive ? "toggle-btn" : "active"}onclick={() => toggleClass()}>
                    <div className="inner-circle"></div>
                </div>
            </div>
            <p>Celsius</p>
        </div>

        <img id="imgLoad" src="./loading-buffering.gif" alt="" srcset=""></img>


        <div class="displayerWeather"></div>
        <img class="weathImg" src="" alt=""></img>

    </div>
  );
}

export default App;
