import './style.scss';
import React, { useState, useEffect } from "react";

function App() {
   const [isActive, setIsActive] = useState(false)
   const [searchValue, setSearchValue] = useState('London')
   const [weather, setWeather] = useState({})
   function toggleClass() {
        setIsActive(!isActive)      
    }


    useEffect(() => {
      ifClicked();
    }, []);


  async function renderData() {
    fetch(
      `http://api.openweathermap.org/data/2.5/weather?q=${searchValue}&appid=1d1d06b0328958cd6e4ba9dc187233d9`,
      {mode: 'cors'})
      .then((res) => {
        if (res.ok) {
          console.log(res.status);
          return res.json();
        } else {
          alert("Oops, there seems to be an error!");
          throw new Error("You have an error");
        }
      })
      .then((object) => {
        setWeather(object);
        console.log(weather);
      })
      .catch((error) => console.log(error));
    }

  return (
    <div className="App">
        <form id="form" action="">
            <label for="userInput">Search location</label>
        <input type="text" className="userInput" name="userInput" value={searchValue} onChange={(e) => setSearchValue(e.target.value)}></input>
        <button type="submit" className="searchBtn" onClick= {()=> renderData()}>Search</button>
        </form>

        <div className="containerToggle">
            <p>Farhenheit</p>
            <div className="container">
                <div className={isActive ? "toggled-btn" : "toggle-btn"} onClick={() => toggleClass()}>
                    <div className="inner-circle"></div>
                </div>
            </div>
            <p>Celsius</p>
        </div>

        <img id="imgLoad" src="./loading-buffering.gif" alt="" srcset=""></img>


        <div className="displayerWeather">
         <p>{weather?.main?.temp}</p>
        </div>
        <img className="weathImg" src="" alt=""></img>

    </div>
  );
}

export default App;
