import './style.scss';
import React, { useState,} from "react";
import clouds from "./images/static/cloudy.svg"
import clearSky from "./images/static/day.svg"
import fewClouds from "./images/static/cloudy-day-2.svg"
import mist from "./images/static/mist.png"
import snow from "./images/static/snowy-6.svg"
import rain from "./images/static/rainy-7.svg"
import thunder from "./images/static/thunder.svg"
import loading from "./images/loading.svg"


function App() {
   const [isActive, setIsActive] = useState(false)
   const [searchValue, setSearchValue] = useState('')
   const [weather, setWeather] = useState({})
   const [loadingLoop, setLoadingLoop] = useState(false);

   function toggleClass() {
        setIsActive(!isActive)      
    }

    // Fetch openweather api and assign the result to weather state
 async function processData() {
   setLoadingLoop(true)
  fetch(`http://api.openweathermap.org/data/2.5/weather?q=${searchValue}&appid=1d1d06b0328958cd6e4ba9dc187233d9`)
    .then(res => { // check result
      if (res.ok) {
        console.log(res.status);
        return res.json();
      } else {
        if (res.status === 404) {
          setLoadingLoop(false)
          setWeather({});
          return alert("Oops, there seems to be an error!(wrong location)");
        }
        alert("Oops, there seems to be an error!");
        setWeather({});
        setLoadingLoop(false)
        throw new Error("You have an error");

      }
    })
    .then(result => { // Assign result to weather state 
      setWeather(result);
      setSearchValue('') 
      setLoadingLoop(false)
    })
    .catch((error) => console.log(error));


    }

  async function processDataKey(e) {
    if(e.key === "Enter") { 
      processData()
     }
    }

    //  Check if Celsius or Farhenheit (toggle button) and display weather data
    function renderData() {
      if(!isActive) {
        return (
          <div className="result-container">
          <div className="weatherLocation">{weather.name}, {weather.sys.country}</div>
          {renderImg()}
          <div className="weatherTemp">{ Math.round((((weather.main.temp -273.15)*1.8)+32 + Number.EPSILON) * 100) / 100 + "°F "}</div>
          <div className="weatherDescr">{weather.weather[0].description}</div>
          <div className="min-max-container">
          <div className="weatherTempMin">{ Math.round((((weather.main.temp_min -273.15)*1.8)+32 + Number.EPSILON) * 100) / 100 + "°F "}</div>
          <div className="weatherTempMax">{ Math.round((((weather.main.temp_max -273.15)*1.8)+32 + Number.EPSILON) * 100) / 100 + "°F "}</div>
          </div>
          <div className="windCard">
          <div className="weatherHumidity">{weather.main.humidity}</div>
          <div className="weatherWindSp">{weather.wind.speed}</div>
          </div>
        </div>
        )
      } else if (isActive) {
      return (
        <div className="result-container">
        <div className="weatherLocation">{weather.name}, {weather.sys.country}</div>
        <div className="weatherTemp">{Math.round((weather.main.temp - 273.15 + Number.EPSILON) * 100) / 100 + "°C "}</div>
        <div className="weatherDescr">{weather.weather[0].description}</div>
        <div className="weatherTempMin">{Math.round((weather.main.temp_min - 273.15 + Number.EPSILON) * 100) / 100 + "°C "}</div>
        <div className="weatherTempMax">{Math.round((weather.main.temp_max - 273.15 + Number.EPSILON) * 100) / 100 + "°C "}</div>
        <div className="weatherHumidity">{weather.main.humidity}</div>
        <div className="weatherWindSp">{weather.wind.speed}</div>
        {renderImg()}
      </div>
      )
      }
    }

    // Check what image to display, regarding the current weather description
      function renderImg() {
        if(weather.weather[0].description.includes("few clouds")) {
          return (<img src={fewClouds} alt="weather"></img>)
      } else if(weather.weather[0].description.includes("clouds")) {
          return (<img src={clouds} alt="weather"></img>)
      } else if(weather.weather[0].description.includes("mist")) {
          return (<img src={mist} alt="weather"></img>)
      } else if(weather.weather[0].description.includes("snow")) {
          return (<img className="weathImg" src={snow} alt="weather"></img>)
      } else if(weather.weather[0].description.includes("clear sky")) {
          return (<img className="weathImg" src={clearSky} alt="weather"></img>)
      } else if(weather.weather[0].description.includes("rain")) {   
          return (<img className="weathImg" src={rain} alt="weather"></img>)
      } else if(weather.weather[0].description.includes("thunderstorm")) {
          return (<img className="weathImg" src={thunder} alt="weather"></img>)
      }
    }  

  return (
    <div className="container-app">
      <div className="inputContainer">
        <label for="userInput">Search location</label>
        <input className="userInput" spellcheck="false" type="text" value={searchValue} onChange={e => setSearchValue(e.target.value)} onKeyPress={processDataKey}/>
        <button type="submit" className="searchBtn"  onClick={processData}>Search</button>
        {loadingLoop ? <img className="loader" src={loading} alt=""></img> : null}
        <div className="containerToggle">
            <p>Farhenheit</p>
            <div className="container">
                <div className={isActive ? "toggled-btn" : "toggle-btn"} onClick={() => toggleClass()}>
                    <div className="inner-circle"></div>
                </div>
             </div>
            <p>Celsius</p>
           </div>
          </div>
          {(typeof weather?.main != "undefined") ? (
            <div>
          {renderData()}
          </div>
            ) : ('')}
    </div>
  );

}

export default App;
