import './style.scss';
import React, { useState,} from "react";
import clouds from "./images/cloudy.svg"
import clearSky from "./images/day.svg"
import fewClouds from "./images/cloudy-day-2.svg"
import mist from "./images/mist.png"
import snow from "./images/snowy-6.svh"



function App() {
   const [isActive, setIsActive] = useState(false)
   const [searchValue, setSearchValue] = useState('')
   const [weather, setWeather] = useState({})
   const [weatherImg, setWeatherImg] = useState('')
   function toggleClass() {
        setIsActive(!isActive)      
    }

 async function processData() {
  fetch(`http://api.openweathermap.org/data/2.5/weather?q=${searchValue}&appid=1d1d06b0328958cd6e4ba9dc187233d9`)
    .then(res => {
      if (res.ok) {
        console.log(res.status);
        return res.json();
      } else {
        if (res.status === 404) {
          return alert("Oops, there seems to be an error!(wrong location)");
        }
        alert("Oops, there seems to be an error!");
        throw new Error("You have an error");
      }
    })
    .then(result => {
      setWeather(result);
      setSearchValue('')
      console.log(result)    
    })
    .catch((error) => console.log(error));
    }

  async function processDataKey(e) {
    if(e.key === "Enter") {
        fetch(`http://api.openweathermap.org/data/2.5/weather?q=${searchValue}&appid=1d1d06b0328958cd6e4ba9dc187233d9`)
          .then(res => {
            if (res.ok) {
              console.log(res.status);
              return res.json();
            } else {
              if (res.status === 404) {
                return alert("Oops, there seems to be an error!(wrong location)");
              }
              alert("Oops, there seems to be an error!");
              throw new Error("You have an error");
            }
          })
          .then(result => {
            setWeather(result);
            setSearchValue('')
            console.log(result)    
          })
          .catch((error) => console.log(error));
     }
    }

    function renderData() {
      if(!isActive) {
        return (
          <div className="result-container">
          <div className="weatherLocation">{weather.name}, {weather.sys.country}</div>
          <div className="weatherTemp">{ Math.round((((weather.main.temp -273.15)*1.8)+32 + Number.EPSILON) * 100) / 100 + "°F "}</div>
          <div className="weatherDescr">{weather.weather[0].description}</div>
          <div className="weatherTempMin">{ Math.round((((weather.main.temp_min -273.15)*1.8)+32 + Number.EPSILON) * 100) / 100 + "°F "}</div>
          <div className="weatherTempMax">{ Math.round((((weather.main.temp_max -273.15)*1.8)+32 + Number.EPSILON) * 100) / 100 + "°F "}</div>
          <div className="weatherHumidity">{weather.main.humidity}</div>
          <div className="weatherWindSp">{weather.wind.speed}</div>
        </div>
        )
      } else if (isActive) {
      return (
        <div className="result-container">
        <div className="weatherLocation">{weather.name}, {weather.sys.country}</div>
        <div className="weatherTemp">{Math.round((weather.main.temp - 273.15 + Number.EPSILON) * 100) / 100 + "°C "}</div>
        <div className="weatherDescr">{weather.weather[0].description}</div>
        <div className="weatherTempMin">{Math.round((weather.main.temp_min - 273.15 + Number.EPSILON) * 100) / 100 + "°C "}</div>
        <div className="weatherTempMax">{Math.round((weather.main.temp_max - 273.15 + Number.EPSILON) * 100) / 100 + "°C "}</div>
        <div className="weatherHumidity">{weather.main.humidity}</div>
        <div className="weatherWindSp">{weather.wind.speed}</div>
      </div>
      )
      }
    }

  return (
    <div className="container-app">
      <div className="inputContainer">
        <label for="userInput">Search location</label>
        <input type="text" value={searchValue} onChange={e => setSearchValue(e.target.value)} onKeyPress={processDataKey}/>
        <button type="submit" className="searchBtn"  onClick={processData}>Search</button>

        <div className="containerToggle">
            <p>Farhenheit</p>
            <div className="container">
                <div className={isActive ? "toggled-btn" : "toggle-btn"} onClick={() => toggleClass()}>
                    <div className="inner-circle"></div>
                </div>
             </div>
            <p>Celsius</p>
           </div>
          </div>
          {(typeof weather?.main != "undefined") ? (
            <div>
          {renderData()}
          <img src={weatherImg} alt="weather"></img>
          </div>
            ) : ('')}
    </div>
  );

}

export default App;
